import React, {useEffect, useState} from 'react';
import axios from "axios";
import {useNavigate} from "react-router-dom";

const EditPullingForm = ({ pulling, onClose }) => {
  const navigate = useNavigate();
  const [formData, setFormData] = useState({
    id: pulling.id,
    name: pulling.name,
    description: pulling.description || '',
    data_source: pulling.data_source,
    header: JSON.stringify(pulling.headers) || '',
    frequency_min: pulling.frequency_min,
    params: pulling.params || ''
  });

  // Function to format header into the desired format
  const formatHeaderToCustomString = (header) => {
    return header.replace(/"(\w+)":/g, '$1:').replace(/":"/g, ':');
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      formData.headers = JSON.parse(formData.headers);
      const response = await axios.put(`http://127.0.0.1:5000/editPullConfig`, formData, {
        headers: {
          'Content-Type': 'application/json',
        },
      });

      if (response.status === 200) {
        alert('Updated Successfully');
        onClose();
        navigate('/pulling');
      }
    } catch (error) {
      alert('Update failed');
      onClose();
      navigate('/pulling');
    }
  };

  return (
      <form onSubmit={handleSubmit}>
          <label>
              Name:
              <input
                  type="text"
                  name="name"
                  value={formData.name}
                  onChange={handleChange}
              />
          </label>

          <label>
              Description:
              <textarea
                  name="description"
                  value={formData.description}
                  onChange={handleChange}
              />
          </label>

          <label>
              Data Source:
              <input
                  type="text"
                  name="data_source"
                  value={formData.data_source}
                  onChange={handleChange}
              />
          </label>

          <label>
              Headers:
              <input
                  type="text"
                  name="headers"
                  value={formData.header}
                  onChange={handleChange}
              />
          </label>

          <label>
              Frequency min:
              <input
                  type="text"
                  name="frequency_min"
                  value={formData.frequency_min}
                  onChange={handleChange}
              />
          </label>

          {/* Add more input fields for other properties as necessary */}

          <button type="submit">Submit</button>
          <button type="button" onClick={onClose}>Cancel</button>
      </form>
  );
};

export default EditPullingForm;
