import React, {useEffect, useState} from 'react';
import axios from "axios";
import {useNavigate} from "react-router-dom";

const EditModelForm = ({ model, onClose }) => {
  const navigate = useNavigate();
  const [formData, setFormData] = useState({
    id: model.id,
    name: model.name,
    description: model.description || '',
    model_file: model.model_file,
    created_date: model.created_date,
    table_configuration_id: model.table_configuration_id,
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      formData.table_configuration_id = parseInt(formData.table_configuration_id, 10);
      const response = await axios.put(`http://localhost:5000/boundary/editModel`, formData, {
        headers: {
          'Content-Type': 'application/json',
        },
      });

      if (response.status === 200) {
        alert('Updated Successfully');
        onClose();
        navigate('/model');
      }
    } catch (error) {
      alert('Update failed');
      onClose();
      navigate('/model');
    }
  };

  return (
      <form onSubmit={handleSubmit}>
          <label>
              Name:
              <input
                  type="text"
                  name="name"
                  value={formData.name}
                  onChange={handleChange}
              />
          </label>

          <label>
              Description:
              <textarea
                  name="description"
                  value={formData.description}
                  onChange={handleChange}
              />
          </label>
          <button type="submit">Submit</button>
          <button type="button" onClick={onClose}>Cancel</button>
      </form>
  );
};

export default EditModelForm;
