import React, {useEffect, useState} from 'react';
import axios from "axios";
import {useNavigate} from "react-router-dom";

const AddPullingForm = ({onClose}) => {
  const navigate = useNavigate();
  const [formData, setFormData] = useState({
    name: '',
    description: '',
    data_source: '',
    headers: '',
    frequency_min: '',
    params: ''
  });

  useEffect(() => {
    // Fetch tasks from Flask backend using Axios
    axios.get('http://127.0.0.1:5000/viewTableType')
      .then(response => {
        console.log(response.data);
      })
      .catch(error => {
        console.error('Error fetching pulling:', error);
      });
  }, []);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post('http://127.0.0.1:5000/addPull', formData, {
        headers: {
    'Content-Type': 'application/json',
        }
})
      if (response.status === 200) {
        alert('Created Successfully');
        onClose();
        navigate('/pulling');
      }
    } catch (error) {
      alert('Created failed');
      onClose();
      navigate('/pulling');
    }
  };

  return (
      <form onSubmit={handleSubmit}>
          <label>
              Name:
              <input
                  type="text"
                  name="name"
                  value={formData.name}
                  onChange={handleChange}
              />
          </label>

          <label>
              Description:
              <textarea
                  name="description"
                  value={formData.description}
                  onChange={handleChange}
              />
          </label>

          <label>
              Data source:
              <input
                  type="text"
                  name="data_source"
                  value={formData.data_source}
                  onChange={handleChange}
              />
          </label>

          <label>
              Header:
              <input
                  type="text"
                  name="header"
                  value={formData.header}
                  onChange={handleChange}
              />
          </label>

          <label>
              Parameters:
              <input
                  type="text"
                  name="params"
                  value={formData.params}
                  onChange={handleChange}
              />
          </label>

          <label>
              Frequency min:
              <input
                  type="text"
                  name="frequency_min"
                  value={formData.frequency_min}
                  onChange={handleChange}
              />
          </label>

          <button type="submit">Submit</button>
          <button type="button" onClick={onClose}>Cancel</button>
      </form>
  );
};

export default AddPullingForm;
