import React, {useEffect, useState} from 'react';
import axios from "axios";
import {useNavigate} from "react-router-dom";

const EditPullingForm = ({ pulling, onClose }) => {
  const navigate = useNavigate();
  const [TType, setTType] = useState([]);
  const [selected_type, setSelected_type] = useState(null);
  const [formData, setFormData] = useState({
    id: pulling.id,
    name: pulling.name,
    description: pulling.description || '',
    data_source: pulling.data_source,
    header: pulling.header || '',
    frequency_min: pulling.frequency_min,
    params: pulling.params || ''
  });

  useEffect(() => {
    // Fetch tasks from Flask backend using Axios
    axios.get('http://127.0.0.1:5000/viewTableType')
      .then(response => {
        setTType(response.data);
        console.log(response.data);
      })
      .catch(error => {
        console.error('Error fetching pulling:', error);
      });
  }, []);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      formData.headers = JSON.parse(formData.headers);
      const response = await axios.put(`http://127.0.0.1:5000/editPullConfig`, formData, {
        headers: {
          'Content-Type': 'application/json',
        },
      });

      if (response.status === 200) {
        alert('Updated Successfully');
        onClose();
        navigate('/pulling');
      }
    } catch (error) {
      alert('Update failed');
      onClose();
      navigate('/pulling');
    }
  };

  return (
      <form onSubmit={handleSubmit}>
          <label>
              Name:
              <input
                  type="text"
                  name="name"
                  value={formData.name}
                  onChange={handleChange}
              />
          </label>

          <label>
              Description:
              <textarea
                  name="description"
                  value={formData.description}
                  onChange={handleChange}
              />
          </label>

          <div>
              <label htmlFor="type">Table Type</label>
              <select
                  id="type"
                  value={selected_type}
                  onChange={(e) => setSelected_type(e.target.value)}
              >
                  <option value="" disabled>Select Table Type</option>
                  {TType.map((type) => (
                      <option key={type.id} value={type.id}>
                          {type.type}
                      </option>
                  ))}
              </select>
              <p>Selected Table Type ID: {selected_type}</p>
          </div>

          <label>
              Frequency min:
              <input
                  type="text"
                  name="frequency_min"
                  value={formData.frequency_min}
                  onChange={handleChange}
              />
          </label>

          <label>
              Data Source:
              <input
                  type="text"
                  name="data_source"
                  value={formData.data_source}
                  onChange={handleChange}
              />
          </label>

          <label>
              Headers:
              <input
                  type="text"
                  name="headers"
                  value={formData.headers}
                  onChange={handleChange}
              />
          </label>

          {/* Add more input fields for other properties as necessary */}

          <button type="submit">Submit</button>
          <button type="button" onClick={onClose}>Cancel</button>
      </form>
  );
};

export default EditPullingForm;
