import React, {useEffect, useState} from 'react';
import axios from "axios";
import {useNavigate} from "react-router-dom";

const AddTableForm = ({onClose}) => {
  const navigate = useNavigate();
  const [MType, setMType] = useState([]);
  const [selected_type, setSelected_type] = useState(null);
  const [formData, setFormData] = useState({
    name: '',
    description:  '',
    table_name: '',
    columns_selected: '',
    model_type_id: '',
    hyper_parameters: ''
  });

  useEffect(() => {
    // Fetch tasks from Flask backend using Axios
    axios.get('http://localhost:5000/boundary/viewModelType')
      .then(response => {
        setMType(response.data);
        console.log(response.data);
      })
      .catch(error => {
        console.error('Error fetching pulling:', error);
      });
  }, []);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
    if (name === 'model_type_id') {
    setFormData({ ...formData, [name]: parseInt(value, 10) || '' }); // Use || '' to handle NaN
      } else {
        setFormData({ ...formData, [name]: value });
      }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      formData.columns_selected = JSON.parse(formData.columns_selected);
      formData.hyper_parameters = JSON.parse(formData.hyper_parameters)
      const response = await axios.post('http://localhost:5000/boundary/addTableConfig', formData, {
        headers: {
    'Content-Type': 'application/json',
        }
})

      if (response.status === 200) {
        alert('Created Successfully');
        onClose();
        navigate('/table');
      }
    } catch (error) {
      alert('Created failed');
      onClose();
      navigate('/table');
    }
  };

  return (
      <form onSubmit={handleSubmit}>
          <label>
              Name:
              <input
                  type="text"
                  name="name"
                  value={formData.name}
                  onChange={handleChange}
              />
          </label>

          <label>
              Description:
              <textarea
                  name="description"
                  value={formData.description}
                  onChange={handleChange}
              />
          </label>

          <label>
              Table Name:
              <input
                  type="text"
                  name="table_name"
                  value={formData.table_name}
                  onChange={handleChange}
              />
          </label>

          <label>
              Columns selected eg.(["a", "b", "c"]):
              <input
                  type="text"
                  name="columns_selected"
                  value={formData.columns_selected}
                  onChange={handleChange}
              />
          </label>

          <div>
              <label htmlFor="type">Model Type</label>
              <select
                  id="type"
                  value={selected_type}
                  onChange={(e) => setSelected_type(e.target.value)}
              >
                  <option value="" disabled>Select Model Type</option>
                  {MType.map((type) => (
                      <option key={type.id} value={type.id}>
                          {type.type_name}
                      </option>
                  ))}
              </select>
              <p>Selected Model Type ID: {selected_type}</p>
          </div>

          <label>
              Hyper parameters:
              <input
                  type="text"
                  name="hyper_parameters"
                  value={formData.hyper_parameters}
                  onChange={handleChange}
              />
          </label>

          {/* Add more input fields for other properties as necessary */}

          <button type="submit">Submit</button>
          <button type="button" onClick={onClose}>Cancel</button>
      </form>
  );
};

export default AddTableForm;
