import React, {useEffect, useState} from 'react';
import axios from "axios";
import {useNavigate} from "react-router-dom";

const DeployConfirmForm = ({model, onClose}) => {
  const navigate = useNavigate();
  const [task, setTask] = useState([]);
  const [selected_task, setSelected_task] = useState(null);
  const [formData, setFormData] = useState({
    id: model.id,
    name: model.name,
    description:  model.description,
    model_file: model.model_file,
    table_configuration_id: model.table_configuration_id
  });

  useEffect(() => {
    // Fetch tasks from Flask backend using Axios
    axios.get('http://localhost:5000/boundary/ViewTask')
      .then(response => {
        setTask(response.data);
        console.log(response.data);
      })
      .catch(error => {
        console.error('Error fetching pulling:', error);
      });
  }, []);

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (selected_task.train_model_id != null) {
        const confirmSubmit = window.confirm("This task is using other Model, proceed to change model?");
        if (confirmSubmit) {
            try {
                const response = await axios.post('http://localhost:5000/boundary/deployModel', formData, {
                    headers: {
                    'Content-Type': 'application/json',
                    }})
                if (response.status === 200) {
                    alert('Deployed Successfully');
                        onClose();
                        navigate('/model');
                }
            } catch (error) {
                alert('Deployed failed');
                onClose();
                navigate('/model');
            }
        } else {
            alert('Deployment Cancelled');
            onClose();
            navigate('/model');
        }
    } else {
        try {
                const response = await axios.post('http://localhost:5000/boundary/deployModel', formData, {
                    headers: {
                    'Content-Type': 'application/json',
                    }})
                if (response.status === 200) {
                    alert('Deployed Successfully');
                        onClose();
                        navigate('/model');
                }
            } catch (error) {
                alert('Deployed failed');
                onClose();
                navigate('/model');
            }
    }

  };

  return (
      <form onSubmit={handleSubmit}>
          <div>
              <label htmlFor="type">Model Type</label>
              <select
                  id="type"
                  value={selected_task}
                  onChange={(e) => setSelected_task(e.target.value)}
              >
                  <option value="" disabled>Select Model Type</option>
                  {task.map((tk) => (
                      <option key={tk.id} value={tk.id}>
                          {tk.name}
                      </option>
                  ))}
              </select>
              <p>Selected Model Type ID: {selected_task}</p>
          </div>

          <button type="submit">Submit</button>
          <button type="button" onClick={onClose}>Cancel</button>
      </form>
  );
};

export default DeployConfirmForm;
